DROP TABLE PEOPLE;

DROP TABLE PRODUCTS;

CREATE TABLE PEOPLE
(SSN            VARCHAR(11),
NAME            VARCHAR(100),
ADDRESS01       VARCHAR(150),
ADDRESS02       VARCHAR(150),
DISTRICT        VARCHAR(50),
CITY            VARCHAR(50),
STATE           VARCHAR(2),
ZIPCODE         VARCHAR(8),
AGE             INT,
GENDER          VARCHAR(1),
CREDIT_LIMIT    FLOAT,
PURCHASE_VOLUME FLOAT,
FIRST_PURCHASE  NUMBER(1));

ALTER TABLE PEOPLE ADD CONSTRAINT PK_PEOPLE PRIMARY KEY (SSN);

ALTER TABLE PEOPLE ADD BIRTH_DATE DATE;

CREATE TABLE PRODUCTS
(ID             VARCHAR (20) ,
NAME            VARCHAR (150) ,
PACKING         VARCHAR (50) ,
SIZEPROD        VARCHAR (50) ,
FLAVOR          VARCHAR (50) ,
PRICE_LIST      FLOAT);

ALTER TABLE PRODUCTS 
ADD CONSTRAINT PK_PRODUCTS 
PRIMARY KEY (ID);






